import React, { useState } from 'react';
import Menu from './Menu';
import Employee from './Employee';
import './App.css';
import MWFLogo from './MWF.svg';
import { Button, Tooltip } from 'carbon-components-react';
import 'carbon-components/css/carbon-components.min.css';
import { BrowserRouter as Router, Switch, Route, Redirect} from 'react-router-dom';



function App() {
  /*function handleClick() {
   console.log("HELLO WORLD");
   }*/

   const [ shouldRedirectMenu, setShouldRedirectMenu] = useState(false);
   const [ shouldRedirectEmployee, setShouldRedirectEmployee] = useState(false);

   if (shouldRedirectMenu == true){
     return(
       <Router>
       <Redirect to={{
         pathname: '/Menu' , 
       }}
       /><Route 
       path="/Menu" component={Menu}/></Router>
     );
   }

   if (shouldRedirectEmployee == true){
    return(
      <Router>
      <Redirect to={{
        pathname: '/Employee' , 
      }}
      /><Route 
      path="/Employee" component={Employee}/></Router>
    );
  }



  return (
    <Router>
    <div className="Manager">
      <header className="Manage-header">
        <img src={MWFLogo} height="350" width="600" />
        <div className="MainMenu" >
          <Button
            className="Menu"
            kind='primary'
            onClick={() => setShouldRedirectMenu(true)}
            >
            Menu
          </Button>
          &nbsp;
          <Button
            className="Employee"
            kind='primary'
            onClick={() => setShouldRedirectEmployee(true)}
            >
            Employee
          </Button>
          </div>
          <div className="MainMenu">
          <Button
            className="Status"
            kind='primary'>
            Status
          </Button>
          &nbsp;
          <Button
            className="Inventory"
            kind='primary'>
            Inventory
          </Button>
          </div>
          <div className="MainMenu">
          <Button
            className="Survey"
            kind='primary'>
            Survey
          </Button>
          &nbsp;
          <Button
            className="Report"
            kind='primary'>
             Report
          </Button>
          </div>


      </header>
    </div>
    </Router>
  );
}

export default App;
